import { useEffect, useState } from "react";
import { Link, Route, Routes, useNavigate } from "react-router-dom";
import {
  AppBar,
  Box,
  Button,
  Container,
  Toolbar,
  Typography,
} from "@mui/material";
import { MainPage } from "../../main_page";

export const AppLayout = () => {
  const [isAppBarFloating, setAppBarFloating] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    function handleScroll() {
      setAppBarFloating(window.pageYOffset > 0);
    }

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const appBarStyles = {
    backgroundColor: "#6F473B",
    transition: "background-color 0.3s ease",
  };

  const linkStyles = {
    textDecoration: "none",
    flexGrow: 1,
    display: "flex",
  };

  const titleStyles = {
    fontFamily: "monospace",
    fontWeight: 700,
    letterSpacing: ".3rem",
    color: "white",
  };

  const buttonStyles = {
    color: "white",
    display: "block",
    marginRight: 2,
  };

  const aboutButtonStyles = {
    color: "white",
    display: "block",
    whiteSpace: "nowrap",
  };

  return (
    <>
      {/* Add CSS to reset the margin on the body element */}
      <style>{`
        body {
          margin: 0;
          paddingLeft: 0;
        }
      `}</style>
      <Box
        sx={{
          width: "100vw",
          height: "100vh",
          overflowX: "hidden",
          margin: 0,
          padding: 0,
        }}
      >
        <AppBar position={isAppBarFloating ? "fixed" : "static"} sx={appBarStyles}>
          <Container maxWidth="xl">
            <Toolbar disableGutters>
              <Link to="/" style={linkStyles}>
                <Typography variant="h6" noWrap sx={titleStyles}>
                  taspa
                </Typography>
              </Link>

              <Box
                sx={{
                  display: "flex",
                  flexGrow: 0,
                  flexDirection: "row",
                  alignItems: "center",
                }}
              >
                <Button component={Link} to="/" sx={buttonStyles}>
                  Home
                </Button>
                <Button component={Link} to="#about" sx={aboutButtonStyles}>
                  About us
                </Button>
              </Box>
            </Toolbar>
          </Container>
        </AppBar>
        <Box sx={{ flexGrow: 1, overflowY: "auto" }}>
          <Routes>
            <Route path="/" exact element={<MainPage />} />
          </Routes>
        </Box>
      </Box>
    </>
  );
};
