            navigate(`/admin/doctors/${response}`)
        } else if(index === 2){
            navigate("/admin/")
        }
    }



    const handleLoginClose = () => {
        setOpenLoginDialog(false)
    }

    const handleLogout = () => {
        logoutUser()
        setIndex(null)
        handleCloseUserMenu()
    }

    useEffect(() => {
        console.log(index)
        if(index === 0) {
            navigate(`/patient/${response}`)
        } else if(index === 1) {
            navigate(`/doctor/${response}`)
        } else if(index === 2){
            navigate(`/doctor/${response}`)
